import React, { useState,useEffect } from "react";
import { useParams } from "react-router-dom";
import Header from "../Header/Header";
import Footer from "../Footer/Footer";
import './DetailsPage.css'

function DetailsPage() {
  const [details, setDetails] = useState([])
  const [author, setAuthor] = useState([])
  console.log(useParams());
  const params = useParams();
  useEffect(() => {
    fetch(`https://openlibrary.org/works/${params.id}.json`)
    .then((response) => response.json())
    .then((detailData) => {
      setDetails(detailData);
      return fetch(`https://openlibrary.org${detailData.authors[0].author.key}.json`)
    })
    .then((response) => response.json())
    .then((authorData) => setAuthor(authorData))
    .catch(console.error)
  },[params.id])
  console.log(details)
  console.log(author)
  return (
    <>
    <Header/>
    <div id="detailsWrapper">


        <div id="cover">
         {details.covers === undefined ? "no image available" : <img src={`https://covers.openlibrary.org/b/id/${details.covers[0]}-L.jpg`} alt='Book Cover' />}
        </div>

        <div id="description">
         <h3>{details.title}</h3>
         <h4>{author.name}</h4>
         <p>{details.description === undefined ? "No Description Available." : (
            typeof details.description === 'object' ? details.description.value : details.description
          )}</p>
        </div>

    </div>
    <Footer/>
    </>
  )}

