import React, { useState,useEffect } from "react";
import { useParams } from "react-router-dom";

function DetailsPage(books) {
  const [details, setDetails] = useState([])
  console.log(useParams());
  const params = useParams();
  useEffect(() => {
    fetch(`http://openlibrary.org/works/${params.id}.json`)
    .then((response) => response.json()
    .then((response) => {setDetails(response)}))
  },[])
  console.log(details)
  console.log(books)
  if ( typeof details.description === 'string') {
    return <div><img src={`https://covers.openlibrary.org/b/id/${books.books.cover_i}-L.jpg`} />{details.title} {details.description}</div>
  } else if (typeof details.description === 'object'){
    return <div>{details.title} {details.description.value}</div>
  } else {
    return <div>{details.title} No Description Available.</div>
  }
}


export default DetailsPage;
